const BLE = require('./BLE');
/*$REQUIRE_PLUGINS$*/
var ble = new BLE("ble", null, false, null, null, false);
/*$PLUGINS$*/
//Connecting internal ports...
//Connecting ports...
ble.stateChangeOnBLEInternalListeners.push(ble.receivestateChangeOnBLEInternalRec.bind(ble));
ble.discoverOnBLEInternalListeners.push(ble.receivediscoverOnBLEInternalRec.bind(ble));
ble.scanStartOnBLEInternalListeners.push(ble.receivescanStartOnBLEInternalRec.bind(ble));
ble.scanStopOnBLEInternalListeners.push(ble.receivescanStopOnBLEInternalRec.bind(ble));
/*$PLUGINS_CONNECTORS$*/
ble._init();
/*$PLUGINS_END$*/
//terminate all things on SIGINT (e.g. CTRL+C)
process.on('SIGINT', function() {
console.log("Stopping components... CTRL+D to force shutdown");
ble._stop();
ble._delete();
/*$STOP_PLUGINS$*/
});

